function fileUploadField(a){new AjaxUpload("#upload_button_"+a,{action:baseUrl+"/file/upload/hoihoi",data:{},name:a,autoSubmit:true,onChange:function(b,c){oldFile=$("#"+a).val();if(oldFile!=""&&oldFile!="existing*"&&oldFile!="*deleted*"){if(!confirm("The old file is deleted when uploading a new file. Do you want to continue?")){return false}}this.setData({field:a,oldFile:oldFile});$("#"+a+"Example").html("Uploading "+createFileHTML(b));$("#"+a+"Delete").hide()},onComplete:function(c,b){if(b==""){$("#"+a).val("");$("#"+a+"Example").html('<span class="error">Error uploading '+createFileHTML(c)+"</span>");$("#"+a+"Delete").hide()}else{$("#"+a).val(b);$("#"+a+"Example").html("Uploaded "+createFileHTML(c));$("#"+a+"Delete").show()}}})}function deleteFile(a){$("#"+a).val("*deleted*");$("#"+a+"Example").html("File deleted");$("#"+a+"Delete").hide()}function createFileHTML(a){return'<a target="_blank" href="'+baseUrl+"/file/get/"+a+'">'+a+"</a>"};